INSERT INTO `gene_intervention_to_vital_process` VALUES (1,254,1,1,4,1,2,0,2,'10.1016/j.cell.2007.05.038','Old AC5 KO mice are protected from aging-induced cardiomyopathy, e.g., hypertrophy, apoptosis, fibrosis, and reduced cardiac function','Старые мыши AC5 KO защищены от вызванной старением кардиомиопатии, например гипертрофии, апоптоза, фиброза и сниженной функции сердца',1,NULL);
