INSERT INTO `gene_intervention_to_vital_process` VALUES (3,254,1,1,2,1,2,0,2,'10.1111/acel.12401','Enhanced exercise capacity in AC5 KO mice was due to improved skeletal muscle function. Knockdown of AC5 in skeletal muscle myoblasts induces increased mitochondrial proteins gene expression and mitochondrial metabolic rate','Повышенная физическая выносливость у мышей AC5 KO была вызвана улучшением функции скелетных мышц. Нокдаун АС5 в миобластах скелетных мышц вызывает увеличение экспрессии генов митохондриальных белков и скорости метаболизма митохондрий',1,NULL);
