INSERT INTO `gene_intervention_to_vital_process` VALUES (6,196,4,1,6,1,NULL,NULL,2,'10.1038/ncb2643','High expression of BubR1 decrease aneuploidization, protects against cancer and attenuates age-related deterioration of select tissues','Высокая экспрессия BubR1 снижает анеуплоидизацию, защищает от рака и уменьшает возрастное ухудшение состояния отдельных тканей',NULL,NULL);
